mapper.insert:
INSERT INTO DH_INSTANCE_DOCUMENT (APP_DOC_UID,APP_DOC_FILENAME,APP_DOC_TITLE,APP_DOC_COMMENT ,DOC_VERSION,APP_UID,TASK_ID,USER_UID,APP_DOC_TYPE,APP_DOC_CREATE_DATE ,APP_DOC_INDEX,APP_DOC_TAGS,APP_DOC_STATUS,APP_DOC_FILEURL ,APP_DOC_IS_HISTORY,APP_DOC_UPDATE_DATE,UPDATE_USER_UID,APP_DOC_ID_CARD) SELECT A.* FROM (
SELECT #{emp.appDocUid} appDocUid, #{emp.appDocFileName} appDocFileName, #{emp.appDocTitle} appDocTitle , #{emp.appDocComment} appDocComment, #{emp.docVersion} docVersion, #{emp.appUid} appUid , #{emp.taskId} taskId, #{emp.userUid} userUid, #{emp.appDocType} appDocType , #{emp.appDocCreateDate} appDocCreateDate, #{emp.appDocIndex} appDocIndex , #{emp.appDocTags} appDocTags, #{emp.appDocStatus} appDocStatus,#{emp.appDocFileUrl} appDocFileUrl , #{emp.appDocIsHistory} appDocIsHistory, #{emp.appDocUpdateDate} appDocUpdateDate , #{emp.updateUserUid} updateUserUid, #{emp.appDocIdCard} appDocIdCard FROM dual
)A

mapper.select:
select * from dh_instance_document 
where APP_UID = #{appUid} and App_Doc_FileName = #{myFileName}
and APP_DOC_STATUS = 'normal' and APP_DOC_IS_HISTORY = 'FALSE'

and app_doc_uid != #{appDocUid}

 select did.app_Doc_Uid,did.app_Doc_FileName,did.app_Doc_FileUrl,did.app_Doc_Title,
 did.app_Doc_Comment,did.doc_Version,did.app_Uid,did.task_id,did.user_uid,did.app_doc_Type
 ,did.app_doc_Create_Date,did.app_doc_index,did.app_doc_tags,did.app_doc_status
 ,did.app_Doc_Is_History,did.app_Doc_Update_Date,did.update_User_Uid,did.app_Doc_Id_Card
 ,update_su.user_name updateUserName
 ,su.user_name as appUserName from dh_instance_document did left join sys_user su 
 on did.USER_UID = su.USER_UID left join sys_user update_su 
 on did.UPDATE_USER_UID = update_su.USER_UID
 where 1 = 1 
 
  and APP_UID = #{appUid}
 
 
  and app_doc_fileName = #{appDocFileName}
 
 
  and app_doc_status = #{appDocStatus}
 
 
  and app_Doc_Is_History = #{appDocIsHistory}
 
 
  and APP_DOC_ID_CARD = #{appDocIdCard}
 
  
  and app_doc_tags = #{appDocTags}
 
 
	 and task_id = #{taskId}
