mapper.insert:
INSERT INTO DH_INSTANCE_DOCUMENT (APP_DOC_UID,APP_DOC_FILENAME,APP_DOC_TITLE,APP_DOC_COMMENT ,DOC_VERSION,APP_UID,TASK_ID,USER_UID,APP_DOC_TYPE,APP_DOC_CREATE_DATE ,APP_DOC_INDEX,APP_D0C_TAGS,APP_DOC_STATUS,APP_DOC_FILEURL) SELECT A.* FROM (
SELECT #{emp.appDocUid} appDocUid, #{emp.appDocFileName} appDocFileName, #{emp.appDocTitle} appDocTitle , #{emp.appDocComment} appDocComment, #{emp.docVersion} docVersion, #{emp.appUid} appUid , #{emp.taskId} taskId, #{emp.userUid} userUid, #{emp.appDocType} appDocType , #{emp.appDocCreateDate} appDocCreateDate, #{emp.appDocIndex} appDocIndex , #{emp.appDocTags} appDocTags, #{emp.appDocStatus} appDocStatus,#{emp.appDocFileUrl} appDocFileUrl FROM dual
)A

mapper.select:
select * from dh_instance_document 
where APP_UID = #{appUid} and App_Doc_FileName = #{myFileName}
and APP_DOC_STATUS = 0

mapper.update:
    begin  
         
            update dh_instance_document 
            
            
                APP_UID = #{item.appUid},
            

            
                APP_DOC_UID = #{item.appDocUid},
            

            
            where id = #{item.id}
            
        ;end;
