mapper.select:
select ,su.user_name as aprUserName, su.station as aprStation,bam.activity_name as activityName, ti.usr_uid as taskHandleUserId,ti.task_delegate_user as agentUserUid from DH_Approval_Opinion ao left join Sys_User su on ao.apr_user_id = su.user_uid left join bpm_activity_meta bam on ao.activity_id = bam.activity_id left join dh_task_instance ti on ao.task_uid = ti.task_uid
and ao.ins_uid = #{insUid}
and ao.activity_id = #{activityId}
order by ao.APR_DATE desc

select * from DH_Approval_Opinion
where 1 = 1

	and ins_uid = #{insUid}


	and activity_id = #{activityId}

order by apr_opi_index asc
