mapper.select:
select  from dh_process_meta 
where category_uid in 

    #{item.categoryUid}

select ,u1.user_name creatorFullName, u2.user_name updatorFullName from dh_process_meta pm left join sys_user u1 on pm.creator = u1.user_uid left join sys_user u2 on pm.update_user = u2.user_uid where pm.category_uid in
#{item.categoryUid}
and pm.pro_name like '%' || #{proName} || '%'

select ,u1.user_name creatorFullName, u2.user_name updatorFullName 
from dh_process_meta pm
    left join sys_user u1 on pm.creator = u1.user_uid
    left join sys_user u2 on pm.update_user = u2.user_uid

    
        pm.PRO_META_UID = #{proMetaUid}
    
    
        and pm.PRO_APP_ID = #{proAppId}
    
    
        and pm.PRO_UID = #{proUid}
    
    
        and pm.PRO_NAME = #{proName}
    
    
        and pm.CATEGORY_UID = #{categoryUid}
